mapper.select:
select distinct user1.*, org1.ORGANSHORTFORM as TRANS_ORGID, dept1.DEPSHORTFORM as TRANS_DEPTID from S_USER user1 left outer join S_ORG org1 on user1.ORGID = org1.ORGID left outer join S_DEPT dept1 on user1.DEPTID = dept1.DEPTID left outer join S_POSTUSER spu on spu.USERID = user1.USERID left outer join S_POST sp on sp.POSTID = spu.POSTID left outer join S_ROLEUSER sru on sru.USERID = user1.USERID left outer join S_ROLE sr on sr.ROLEID = sru.ROLEID
user1.RECORDSTATE='0'
${whereCondition}
order by user1.STATE
,${order}

select count(distinct user1.RID) NUM from S_USER user1 left outer join S_ORG org1 on user1.ORGID = org1.ORGID left outer join S_DEPT dept1 on user1.DEPTID = dept1.DEPTID left outer join S_POSTUSER spu on spu.USERID = user1.USERID left outer join S_POST sp on sp.POSTID = spu.POSTID left outer join S_ROLEUSER sru on sru.USERID = user1.USERID left outer join S_ROLE sr on sr.ROLEID = sru.ROLEID
${whereCondition}
and user1.RECORDSTATE='0'

mapper.update:

	update S_USER set                     
                        RECORDSTATE=#{RECORDSTATE}
	where RID=#{RID}


	update S_USER set                     
                        DEPTID=#{DEPTID}
	where USERID=#{USERID}

update S_USER
USERID = #{USERID}, USERNAME = #{USERNAME}, NICKNAME = #{NICKNAME}, LOGINCODE = #{LOGINCODE}, LOGINPASSWORD = #{LOGINPASSWORD}, STARTDATE = #{STARTDATE}, PASSWVALDA = #{PASSWVALDA}, FIREDATE = #{FIREDATE}, BIRTHDAY = #{BIRTHDAY}, TELNUM = #{TELNUM}, IDCARDNO = #{IDCARDNO}, LASTLOGDAT = #{LASTLOGDAT}, RETIREDATE = #{RETIREDATE}, USERMAIL = #{USERMAIL}, WRONGPINNUM = #{WRONGPINNUM}, MEMO = #{MEMO}, STATE = #{STATE}, IPMASK = #{IPMASK}, ORDERNO = #{ORDERNO}, QUESTION = #{QUESTION}, ANSWER = #{ANSWER}, MODIFIEDTIME = #{MODIFIEDTIME}, MODIFIERID = #{MODIFIERID}, LIABMAN = #{LIABMAN}, LIABORG = #{LIABORG}, RECORDSTATE = #{RECORDSTATE}, ORGID=#{ORGID}, DEPTID=#{DEPTID}, IFHYPOTHEC=#{IFHYPOTHEC}, HYPOTHECQUOTA=#{HYPOTHECQUOTA}, OCCUPYAMT=#{OCCUPYAMT},
where RID=#{RID}

update S_USER
USERID = #{USERID}, USERNAME = #{USERNAME}, NICKNAME = #{NICKNAME}, LOGINCODE = #{LOGINCODE}, LOGINPASSWORD = #{LOGINPASSWORD}, STARTDATE = #{STARTDATE}, PASSWVALDA = #{PASSWVALDA}, FIREDATE = #{FIREDATE}, BIRTHDAY = #{BIRTHDAY}, TELNUM = #{TELNUM}, IDCARDNO = #{IDCARDNO}, LASTLOGDAT = #{LASTLOGDAT}, RETIREDATE = #{RETIREDATE}, USERMAIL = #{USERMAIL}, WRONGPINNUM = #{WRONGPINNUM}, MEMO = #{MEMO}, STATE = #{STATE}, IPMASK = #{IPMASK}, ORDERNO = #{ORDERNO}, QUESTION = #{QUESTION}, ANSWER = #{ANSWER}, MODIFIEDTIME = #{MODIFIEDTIME}, MODIFIERID = #{MODIFIERID}, LIABMAN = #{LIABMAN}, LIABORG = #{LIABORG}, RECORDSTATE = #{RECORDSTATE}, ORGID=#{ORGID}, DEPTID=#{DEPTID}, IFHYPOTHEC=#{IFHYPOTHEC}, HYPOTHECQUOTA=#{HYPOTHECQUOTA}, OCCUPYAMT=#{OCCUPYAMT}
where RID=#{RID}


update S_USER set
	 IFHYPOTHEC=#{IFHYPOTHEC},
	 HYPOTHECQUOTA=#{HYPOTHECQUOTA},
	 OCCUPYAMT=#{OCCUPYAMT}
where USERID=#{USERID}

mapper.select:

	select usr.* from S_USER usr 
	 usr.RECORDSTATE = '0' AND
	      exists( select userid from s_postuser pu 


              pu.USERID = usr.USERID AND pu.POSTID = #{INCEPTPOST}



	      )

             and usr.ORGID = #{INCEPTORGID}


             and usr.DEPTID = #{INCEPTDEPTID}
